import React, {Component} from 'react'
import DatePicker from 'react-datepicker'
import 'react-datepicker/dist/react-datepicker.css'
import axios from 'axios'

export default class EditExercise extends Component{
    constructor(props){
        super(props)

        this.onChangeWorkout = this.onChangeWorkout.bind(this)
        this.onChangeReps = this.onChangeReps.bind(this)
        this.onChangeWeight = this.onChangeWeight.bind(this)
        this.onChangeDate = this.onChangeDate.bind(this)
        this.onSubmit = this.onSubmit.bind(this)

        this.state={
            workout: '',
            reps: 0,
            weight: 0,
            date: new Date(),
            users: []
        }

    }

    componentDidMount(){
        axios.get('http://localhost:5000/exercises/'+this.props.match.params.id)
        .then(response=>{
            this.setState({
                workout: response.data.workout,
                reps: response.data.reps,
                weight: response.data.weight,
                date: new Date(response.data.date)
            })
            
        })
        .catch((error)=>{
            console.log(error)
        })
        axios.get('http://localhost:5000/workouts/')
        .then(res => {
            if(res.data.length>0){
                this.setState({
                    workout: res.data.map(w=>w.workout)
                })
            }
        })
    }

    onChangeWorkout(e){
        this.setState({
            workout: e.target.value
        })
    }
    onChangeReps(e){
        this.setState({
            reps: e.target.value
        })
    }
    onChangeDuration(e){
        this.setState({
            duration: e.target.value
        })
    }
    onChangeDate(date){
        this.setState({
            date: date
        })
    }
    onSubmit(e){
        e.preventDefault()

        const exercise = {
            username: this.state.username,
            description: this.state.description,
            duration: this.state.duration,
            date: this.state.date
        }

        console.log(exercise)

        axios.post('http://localhost:5000/exercises/update/'+ this.props.match.params.id, exercise)
        .then(res=>console.log(res.data))

        window.location = '/'
    }
    render(){
        return(
            <div>
                <h3>Edit Exercise Log</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Username: </label>
                        <select
                            ref="userInput"
                            required
                            className="form-control"
                            value={this.state.username}
                            onChange={this.onChangeUsername}
                        >{
                            this.state.users.map(function(user){
                                return <option 
                                    key ={user}
                                    value ={user}>{user}
                                    </option>
                            })
                        }</select>

                    </div>
                    <div className="form-group">
                        <label>Description: </label>
                        <input
                            type="text"
                            required
                            className="form-control"
                            value={this.state.description}
                            onChange={this.onChangeDescription}
                            />

                    </div>
                    <div className="form-group">
                        <label>Duration(in minutes): </label>
                        <input
                            type="text"
                            required
                            className="form-control"
                            value={this.state.duration}
                            onChange={this.onChangeDuration}
                            />

                    </div>
                    <div className="form-group">
                        <label style={{paddingRight: 10}}>Date: </label>
                        <DatePicker
                            selected={this.state.date}
                            onChange={this.onChangeDate}
                        
                        />

                    </div>
                    <div className="form-group">
                        <button type="submit" className="btn btn-dark">Edit Exercise Log</button>
                    </div>
                </form>
            </div>
        )
    }
}
