import React, {Component} from 'react'
import DatePicker from 'react-datepicker'
import 'react-datepicker/dist/react-datepicker.css'
import axios from 'axios'

export default class CreateExercise extends Component{
    constructor(props){
        super(props)

        this.onChangeUsername = this.onChangeUsername.bind(this)
        this.onChangeDescription = this.onChangeDescription.bind(this)
        this.onChangeDuration = this.onChangeDuration.bind(this)
        this.onChangeDate = this.onChangeDate.bind(this)
        this.onSubmit = this.onSubmit.bind(this)

        this.state={
            workout: '',
            reps: '',
            weight: 0,
            date: new Date(),
            workouts: []
        }

    }

    componentDidMount(){
        axios.get('http://localhost:5000/workouts/')
        .then(res => {
            if(res.data.length>0){
                this.setState({
                    workouts: res.data.map(workout=>workout.workout),
                    workout: res.data[0].workout
                })
            }
        })
    }

    onChangeUsername(e){
        this.setState({
            username: e.target.value
        })
    }
    onChangeDescription(e){
        this.setState({
            description: e.target.value
        })
    }
    onChangeDuration(e){
        this.setState({
            duration: e.target.value
        })
    }
    onChangeDate(date){
        this.setState({
            date: date
        })
    }
    onSubmit(e){
        e.preventDefault()

        const exercise = {
            workout: this.state.workout,
            reps: this.state.reps,
            weight: this.state.weight,
            date: this.state.date
        }

        console.log(exercise)

        axios.post('http://localhost:5000/exercises/add', exercise)
        .then(res=>console.log(res.data))

        window.location = '/'
    }
    render(){
        return(
            <div>
                <h3>Create new Exercise Log</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Username: </label>
                        <select
                            ref="userInput"
                            required
                            className="form-control"
                            value={this.state.username}
                            onChange={this.onChangeUsername}
                        >{
                            this.state.users.map(function(user){
                                return <option 
                                    key ={user}
                                    value ={user}>{user}
                                    </option>
                            })
                        }</select>

                    </div>
                    <div className="form-group">
                        <label>Description: </label>
                        <input
                            type="text"
                            required
                            className="form-control"
                            value={this.state.description}
                            onChange={this.onChangeDescription}
                            />

                    </div>
                    <div className="form-group">
                        <label>Duration(in minutes): </label>
                        <input
                            type="text"
                            required
                            className="form-control"
                            value={this.state.duration}
                            onChange={this.onChangeDuration}
                            />

                    </div>
                    <div className="form-group">
                        <label style={{paddingRight: 10}}>Date: </label>
                        <DatePicker
                            selected={this.state.date}
                            onChange={this.onChangeDate}
                        
                        />

                    </div>
                    <div className="form-group">
                        <button type="submit" className="btn btn-dark">Create Exercise Log</button>
                    </div>
                </form>
            </div>
        )
    }
}
